(()=>{document.addEventListener("DOMContentLoaded",()=>{let s=document.querySelector(".site-header"),r=window.scrollY,t=!0;window.addEventListener("scroll",()=>{let e=window.scrollY;e>r?t||(s.classList.add("hide"),t=!0):(t||e===0)&&(s.classList.remove("hide"),t=!1),r=e});let c=document.getElementById("menu-toggle"),n=document.getElementById("menu-drawer"),d=document.getElementById("menu-close");c.addEventListener("click",function(){n.classList.add("open")}),d.addEventListener("click",function(){n.classList.remove("open")}),document.addEventListener("click",function(e){!n.contains(e.target)&&!c.contains(e.target)&&n.classList.remove("open")});let a=document.querySelectorAll(".bar"),i=new IntersectionObserver(e=>{e.forEach(l=>{if(l.isIntersecting){let o=l.target,u=o.getAttribute("data-years"),m=o.getAttribute("data-colors");o.style.width=`${u*5}%`,o.style.backgroundColor=m}})},{threshold:.5});a.forEach(e=>i.observe(e))});})();
